ArelB.map:
function(doc) {
  for(var edgeName in doc.hasList)
    for(var node in doc.hasList[edgeName])
      emit(doc.name + " has " + edgeName + ": " + doc.hasList[edgeName][node], doc.name);

  for(var edgeName in doc.isOfList)
    for(var node in doc.isOfList[edgeName])
      emit(doc.name + " isof " + edgeName + ": " + doc.isOfList[edgeName][node], doc.name);
}

