ArelB.map:
function(doc) {
  if(doc.type == "edge"){
    emit(doc.source + " " + doc.dir + " " + doc.name + ": " + doc.target,doc.source);
    emit(doc.target + " " + (doc.dir == "has" ? "isof" : "has") + " " + doc.name + ": " + doc.source,doc.target);
  }
}

relB.map:
function(doc) {
  if(doc.type == "edge"){
    emit(doc.dir + " " + doc.name + ": " + doc.target,doc.source);
    emit((doc.dir == "has" ? "isof" : "has") + " " + doc.name + ": " + doc.source,doc.target);
  }
}

AconnB.map:
function(doc) {
  if(doc.type == "edge"){
    emit(doc.source + ";" + doc.target,doc.source);
    emit(doc.target + ";" + doc.source,doc.target);
  }
}

connB.map:
function(doc) {
  if(doc.type == "edge"){
    emit(doc.target,doc.source);
    emit(doc.source,doc.target);
  }
  else if(doc.type == "node"){
    emit(doc.name,doc.name);
  }
}
