ArelB.map:
function(doc) {
  for(var edgeName in doc.has)
    for(var node in doc.has[edgeName])
      emit(doc.name + " has " + edgeName + ": " + doc.has[edgeName][node], doc.name);

  for(var edgeName in doc.isof)
    for(var node in doc.isof[edgeName])
      emit(doc.name + " isof " + edgeName + ": " + doc.isof[edgeName][node], doc.name);
}

