AconnB_fancy.map:
function(doc) {
  var dirs = ["has","isof"];
  emit(doc.name + " " + doc.name, doc.name);
  for(var dir in dirs)
    for(var edgeName in doc[dirs[dir]])
      for(var node in doc[dirs[dir]][edgeName])
        emit(doc.name + " " + doc[dirs[dir]][edgeName][node], doc.name);
}

incoming.map:
function(doc) {
  var dirs = ["has","isof"];
  for(var dir in dirs){
    dir = dirs[dir];
    for(var edgeName in doc[dir])
      for(var node in doc[dir][edgeName])
        emit(doc[dir][edgeName][node], {"node":doc.name, "dir":dir, "edge":edgeName});
  }
}
